import React, { Component } from 'react'

 class Todoapplist extends Component {
  render() {
    return (
      <div>
        <ul class="ul">
          {this.props.items.map((item,index)=> {
            return <li class="list"  > 
            <div class="list-items"><input id="myCheckBox" type="checkbox" class="checkbox"></input>
               <span>{item}</span></div><button  class="deletebtn" key ={index} style={{
                textDecoration: this.state.completed[index]
                  ? "line-through"
                  : ""
              }} ><i onClick={this.props.deleteTask.bind(this,index)}  class="fa fa-trash" ></i></button> 
           </li>
          }

          )}
        </ul>
      </div>
    )
  }
}

export default Todoapplist


