import React, { Component } from 'react'
import Todoapplist from './Todoapplist'

const h2 =document.getElementById('h2');

 class Todoapp extends Component {



   constructor(props) {
     super(props)

     this.state = {
        term:'',
        items:[]
     }
     h2.style.display = 'none';
   }

   onChangeHandler = (e) =>{
       this.setState({
        term: e.target.value
       })

   }

   onFormhandler =(f) =>{
     f.preventDefault();
     this.setState({
      term: '',
      items:[...this.state.items,this.state.term]


     })

   }

   onDeleteHandler = (index) =>{
    const deleteTask =[...this.state.items];
    deleteTask.splice(index,1);
    this.setState({
      items:deleteTask
    })
   }







  render() {

    return (

      <div>
        <h1>All Tasks</h1>
        <form>
        <div>
           <input id="input" type="text" name="add" class="textinput" placeholder="Add a new task"  value ={this.state.term} onChange={this.onChangeHandler} />
           <button id="addbtn" class="button" onClick={this.onFormhandler}>Add</button>

        </div>
        </form>
        <div>
          < Todoapplist deleteTask={this.onDeleteHandler} items ={this.state.items} />
        </div>
        <div>
            <h2 id="h2">Seems like a quiet day</h2>
        </div>
      </div>

    )
  }
}


export default Todoapp
