import React, { Component } from 'react'

 class Todoapplist extends Component {
  render() {
    return (
      <div>
        <ul class="ul">
          {this.props.items.map((item,index)=> (
            <li class="list" key ={index} >{item} 
            <button onClick={this.props.deleteTask.bind(this,index)} class="deletebtn"><i class="fa fa-trash" ></i></button> </li>
          )

          )}
        </ul>
      </div>
    )
  }
}

export default Todoapplist


